import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Producto = () => {

    const [productos, setProductos] = useState([]);
    const [productos2, setProductos2] = useState([]);

    const showing = false;
    const [id, setID] = useState("");
    const [descripcion, setDescripcion] = useState("");

    useEffect(() => {
        getProductos();
    }, []);

    const getProductos = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/productos/get');
            console.log(response.data.products);
            setProductos(response.data.products);
            setProductos2(response.data.products);
        } catch (error) {
            console.log("Ha ocurrido un error");
        }
    };

    const buscarProducto = (e) => {          
        productos.forEach((producto) => {
            if (producto.id.includes(id)) {
                setProductos2([producto]);
            }
        })      
    }

    const buscarDescripcion = (e) => {          
        productos.forEach((producto) => {
            if (producto.descripcion.includes(descripcion)) {
                setProductos2([producto]);
            }
        })      
    }

    const changeID = (e) => {
        let id = e.target.value;
        setID(id);
        if (id.toString() === ""){
            setProductos2(productos);
        }
    }

    return (
        <div>
            <div class="form-inline offset-sm-1 mt-5">
                <input class="form-control mr-sm-2" name="uuid" type="number" placeholder="Identificador" aria-label="UUID" onChange={changeID}/>
                <button class="btn btn-outline-success my-2 my-sm-0" onClick={buscarProducto}>Buscar</button>
            </div>
            <div class="form-inline offset-sm-1 mt-5">
                <input class="form-control mr-sm-2" name="uuid" type="number" placeholder="Identificador" aria-label="UUID" onChange={changeID}/>
                <button class="btn btn-outline-success my-2 my-sm-0" onClick={buscarProducto}>Buscar</button>
            </div>
            <div class="col-sm-10 offset-sm-1 p-4 rounded">                
                <h2 class="mg-l text-left mb-2">Productos:</h2> 
                <table class="table">
                    <thead class="thead-dark">
                        <tr>
                        <th scope="col">Identificador</th>
                        <th scope="col">Nombre</th>
                        <th scope="col">Descripci√≥n</th>
                        <th scope="col">Valor Unitario</th>
                        <th scope="col">Estado</th>
                        </tr>
                    </thead>
                    <tbody>
                        { 
                            productos2.map((product) => (                                
                                <tr>
                                    <td>{product.id}</td>
                                    <td>{product.name}</td>
                                    <td>{product.descripcion}</td>
                                    <td>{product.valor}</td>
                                    {product.stock === 1 && <td>Disponible</td>}
                                    {product.stock === 0 && <td>No disponible</td>}
                                </tr>
                            )) 
                        }
                    </tbody>
                </table>
            </div>
        </div>

    );
}

export default Producto;
