import React, { useEffect, useState } from 'react';

const ModificarProducto = () => {

    const [users, setusers] = useState([]);

    useEffect(() => {
        getUsersPendientes();
    }, []);


    const getUsersPendientes = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/pendientes');
            console.log(response.data.users);
            setusers(response.data.users);
        } catch (error) {
            console.log("Ha ocurrido un error");
        }
    }

    const getInitialState = () => {
        const value = "correo";
        return value;
    };
    
    const [producto, setProducto] = useState(getInitialState);

    const selectProduct = (e) => {    
        const user = JSON.parse(e.target.value); 
        setProducto(user);
    };

    return (        
         <div class="modal-dialog text-center">
            <div class="col-sm-8 main-section">
                <div class="modal-content">
                    <div class="col-12 user-img">
                        <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR6MlkOcfm3WFyiWvOGuk0U0ZpsYaTcjNfjzF60r5-jVk4a-W-N1IXfcLZLwna6ngmfDx8&usqp=CAU" alt="icon" />
                    </div>
                    <form class="row g-3">
                        <h4 class="col-12">Modificar producto: </h4>              
                        <select class="form-select" name="emailSelector" onChange={selectUser}> 
                        <option value={null}></option>                 
                        {
                            users.map((user) => (
                            <option value={JSON.stringify(user)}>{user.email}</option>
                            ))                    
                        }
                        </select>
                        <div class="col-12 mt-2">
                            <label for="nombre" class="form-label">Nombre:</label>
                            <input type="text" class="form-control" id="nombre" name="nombre" />
                        </div>
                        <div class="col-12">
                            <label for="descripcion" class="form-label">Descripci√≥n:</label>
                            <input type="text" class="form-control" id="descripcion" name="descripcion" />
                        </div>
                        <div class="col-12">
                            <label for="valor" class="form-label">Valor unitario:</label>
                            <input type="number" class="form-control" id="valor" name="valor" />
                        </div>
                        <div class="col-12">
                            <label for="estado" class="form-label">Estado del producto:</label>
                            <select class="form-select" name="estado">
                                <option value="0" selected>Disponible</option>
                                <option value="1" selected>No disponible</option>
                            </select>
                        </div>
                        <div class="col-12 mt-4 mb-4">
                            <button class="btn btn-dark btn-block" id="post-btn" type="submit">Registrar</button>
                        </div>
                    </form>
                </div>
            </div>
        </div>        
    );
}

export default ModificarProducto;
