import { createContext, useState, useCallback, useMemo } from "react";
import PropTypes from 'prop-types';

const LOGIN_ADMIN = 'LOGIN_STATE';


export const AuthContext = createContext();

export default function AuthContextProvider({children}) {    

    const [isAuthenticated, setIsAuthenticated] = useState(window.localStorage.getItem(LOGIN_STATE));

    const login = useCallback(() => {
        window.localStorage.setItem(LOGIN_STATE, true);
        setIsAuthenticated(true);
    }, []);

    const logout = useCallback(() => {
        window.localStorage.removeItem(LOGIN_STATE, true);
        setIsAuthenticated(false);
    }, []);

    const value = useMemo(() => ({
        isAuthenticated,
        login,
        logout
    }), [isAuthenticated, login, logout]);

    return (
        <AuthContext.Provider value={value}>
            {children}
        </AuthContext.Provider>
    );
}

AuthContextProvider.propTypes = {
    children: PropTypes.object
};
