import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Producto = () => {

    const [productos, setProductos] = useState([]);

    useEffect(() => {
        getProductos();
    }, []);

    const showing = false;

    const getProductos = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/productos/get');
            console.log(response.data.products);
            setProductos(response.data.products);
        } catch (error) {
            console.log("Ha ocurrido un error");
        }
    };

    const buscarProducto = (e) => {
        console.log(e.target.value);
    }


    return (
        <div>
            <input class="form-control mr-sm-2" name="uuid" type="search" placeholder="Identificador" aria-label="UUID" />
            <button class="btn btn-outline-success my-2 my-sm-0" onClick={buscarProducto}>Buscar</button>
            <div class="col-sm-10 offset-sm-1 p-4 rounded">                
                <h2 class="mg-l text-left mb-2">Productos:</h2> 
                <table class="table">
                    <thead class="thead-dark">
                        <tr>
                        <th scope="col">Identificador</th>
                        <th scope="col">Nombre</th>
                        <th scope="col">Descripción</th>
                        <th scope="col">Valor Unitario</th>
                        <th scope="col">Estado</th>
                        </tr>
                    </thead>
                    <tbody>
                        { 
                            productos.map((product) => (                                
                                <tr>
                                    <td>{product.id}</td>
                                    <td>{product.name}</td>
                                    <td>{product.descripcion}</td>
                                    <td>{product.valor}</td>
                                    {product.stock === 1 && <td>Disponible</td>}
                                    {product.stock === 0 && <td>No disponible</td>}
                                </tr>
                            )) 
                        }
                    </tbody>
                </table>
            </div>
        </div>

    );
}

export default Producto;
